    def __init__(self, parent=None):
        super(MainWindow, self).__init__(parent)
        self.setupUi(self)
        self.linkButton.clicked.connect(self.setLinkUrl)
        # self.start.clicked.connect(self.startData)

    @pyqtSlot()
    def setLinkUrl(self):
        print("linking...")
        globalVar.set_value("url",self.linkeEdit.text())
        globalVar.set_value("linkFlag",True)
        t = threading.Thread(target=self.listenMessage,)
        t.start()
    def listenMessage(self):
        while globalVar.get_value("linkFlag"):
            item = globalVar.get_value("queue").get()
            print("173:"+str(item))
            if item['type'] != "":
                index = globalVar.get_value("index")
                print(index)
                self.tableUI.setRowCount(index+1)
                self.tableUI.setItem(index, 0, QtWidgets.QTableWidgetItem(str(index+1)))
                self.tableUI.setItem(index, 1, QtWidgets.QTableWidgetItem(item['id']))
                self.tableUI.setItem(index, 2, QtWidgets.QTableWidgetItem(item['shortId']))
                self.tableUI.setItem(index, 3, QtWidgets.QTableWidgetItem(item['nickname']))
                self.tableUI.setItem(index, 4, QtWidgets.QTableWidgetItem(item['type']))
                self.tableUI.setItem(index, 5, QtWidgets.QTableWidgetItem(item['content']))
                self.tableUI.setItem(index, 6, QtWidgets.QTableWidgetItem(item['gender']))
                self.tableUI.setItem(index, 7, QtWidgets.QTableWidgetItem( str(item['payGradeLevel'])+"级-" + item['alternativeText']))
                self.tableUI.setItem(index, 8, QtWidgets.QTableWidgetItem(item['followerCount']))
                self.tableUI.setItem(index, 9, QtWidgets.QTableWidgetItem(item['followingCount']))
                self.tableUI.viewport().update()
                globalVar.set_value("index", index+1)




